operations.body:
query SearchQuery($query: String!) {
  search(query: $query) {
    id
    title
    rating
    price
    thumbnail
    __typename
  }
}

query Products($category: Category!, $sortBy: String, $order: Order, $limit: Int, $skip: Int) {
  products(
    category: $category
    sortBy: $sortBy
    order: $order
    limit: $limit
    skip: $skip
  ) {
    limit
    skip
    total
    results {
      id
      title
      rating
      price
      thumbnail
      __typename
    }
    __typename
  }
  categories {
    name
    slug
    __typename
  }
}

query Product($id: ID!) {
  product(id: $id) {
    id
    title
    rating
    price
    description
    images
    __typename
  }
}

mutation AddToCart($productId: ID!, $quantity: Int!) {
  addToCart(productId: $productId, quantity: $quantity) {
    id
    __typename
  }
}

query TopProducts {
  topProducts {
    id
    title
    rating
    price
    thumbnail
    __typename
  }
  categories {
    image
    name
    slug
    __typename
  }
}

mutation UpdateCartItemQuantity($cartItemId: ID!, $quantity: Int!) {
  updateCartItemQuantity(id: $cartItemId, quantity: $quantity) {
    id
    quantity
    __typename
  }
}

query CartQuery {
  cart {
    id
    quantity
    product {
      price
      thumbnail
      title
      __typename
    }
    __typename
  }
}
